mapper.select:
select 

from t_order_apply
where 1 = 1 

  and transporter_id  = #{transporterId,jdbcType=VARCHAR}


  and order_id  = #{orderId,jdbcType=VARCHAR}

select count(1)
from t_order_apply
where 1 = 1 

  and order_id  = #{orderId,jdbcType=VARCHAR}


  and status  = #{status,jdbcType=INTEGER}
