mapper.select:
select
 a.id, a.create_date, a.update_date, a.data_status, a.desp, a.order_id, a.shipper_id, a.transporter_id, 
 a.is_transfer, a.transfer_transporter_id, a.attachment_take_id, a.attachment_deliver_id, 
 a.status, a.costs, a.order_no,
 concat('${@com.iflytransporter.api.conf.ConstantsConfig@getLinkPath()}',b.attachment_link)  as attachment_take_id_link,
 concat('${@com.iflytransporter.api.conf.ConstantsConfig@getLinkPath()}',c.attachment_link)  as attachment_deliver_id_link
  from t_waybill a
 left join t_attachment b on a.attachment_take_id = b.id
 left join t_attachment c on a.attachment_deliver_id = c.id
 where 1 = 1 

   and shipper_id  = #{shipperId,jdbcType=VARCHAR}


   and transporter_id  = #{transporterId,jdbcType=VARCHAR}


   and shipper_id  = #{shipperId,jdbcType=VARCHAR}


   and shipper_company_id  = #{shipperCompanyId,jdbcType=VARCHAR}


   and transporter_company_id  = #{transporterCompanyId,jdbcType=VARCHAR}


   and status  = #{status,jdbcType=VARCHAR}

select 

from t_waybill
where 1 = 1 

  and shipper_id  = #{shipperId,jdbcType=VARCHAR}


  and transporter_id  = #{transporterId,jdbcType=VARCHAR}


  and shipper_id  = #{shipperId,jdbcType=VARCHAR}


  and shipper_company_id  = #{shipperCompanyId,jdbcType=VARCHAR}


  and transporter_company_id  = #{transporterCompanyId,jdbcType=VARCHAR}


  and status  = #{status,jdbcType=VARCHAR}
