mapper.select:
select a.id, a.create_date as createDate, a.id, a.update_date as updateDate, a.serve_grade as serveGrade, a.loading_grade as loadGrade, a.goods_grade goodsGrade,a.content, a.anonymity,a.order_no as orderNo, b.mobile as transporterMobile,b.surname,b.name, c.mobile as shipperMobile, e.name as shipperCompanyName
concat('${@com.iflytransporter.web.conf.ConstantsConfig@getLinkPath()}',a1.attachment_link) as attachmentId1Link, concat('${@com.iflytransporter.web.conf.ConstantsConfig@getLinkPath()}',a2.attachment_link) as attachmentId2Link, concat('${@com.iflytransporter.web.conf.ConstantsConfig@getLinkPath()}',a3.attachment_link) as attachmentId3Link concat('${@com.iflytransporter.web.conf.ConstantsConfig@getLinkPath()}',a4.attachment_link) as attachmentId4Link from t_transporter_comment a left join t_user b on a.transporter_id = b.id left join t_user c on a.shipper_id = c.id left join t_company e on a.shipper_company_id = e.id
left join t_attachment a1 on a.attachment_id_1 = a1.id left join t_attachment a2 on a.attachment_id_2 = a2.id left join t_attachment a3 on a.attachment_id_3 = a3.id left join t_attachment a4 on a.attachment_id_4 = a4.id where a.id = #{id,jdbcType=VARCHAR}
